import React from 'react'
import ReactDOM from 'react-dom'

const Header = (props) => {
	return (
		<h1> {props.coursename} </h1>
	)
}

const Part = (props) => {
	return (
		<p> {props.name} {props.count} </p>
	)
}

const Content = (props) => {
	return (
		<div>
			<Part name={props[0].name} count={props[0].exercises} />
			<Part name={props[1].name} count={props[1].exercises} />
			<Part name={props[2].name} count={props[2].exercises} />
		</div>
	)
}

const Total = (props) => {
	return (
		<p> Number of exercises {props[0].exercises + props[1].exercises + props[2].exercises} </p>
	)
}


const App = () => {
  const course = 'Half Stack application development'
  const parts = [
  {
  	name: 'Fundamentals of React',
  	exercises: 10
  },
  {
  	name: 'Using props to pass data',
  	exercises: 7
  },
  {
  	name: 'State of a component',
  	exercises: 14
  }
  ]

  return (
    <div>
      <Header coursename = {course} />
      <Content parts = {parts} />
      <Total parts = {parts} />
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
