import React from 'react'
import ReactDOM from 'react-dom'

const Header = (props) => {
	return (
		<h1> {props.coursename} </h1>
	)
}

const Part = (props) => {
	return (
		<p> {props.name} {props.count} </p>
	)
}

const Content = (props) => {
	return (
		<div>
			<Part name={props.name1} count={props.count1} />
			<Part name={props.name2} count={props.count2} />
			<Part name={props.name3} count={props.count3} />
		</div>
	)
}

const Total = (props) => {
	return (
		<p> Number of exercises {props.count} </p>
	)
}


const App = () => {
  const course = 'Half Stack application development'
  const parts = [
  {
  	name: 'Fundamentals of React',
  	exercises: 10
  },
  {
  	name: 'Using props to pass data',
  	exercises: 7
  },
  {
  	name: 'State of a component',
  	exercises: 14
  }
  ]

  return (
    <div>
      <Header coursename = {course} />
      <Content name1 = {parts[1].name} count1 = {parts[1].exercises}
      		   name2 = {parts[2].name} count2 = {parts[2].exercises} 
      		   name3 = {parts[3].name} count3 = {parts[3].exercises} />
      <Total count = {parts[1].exercises + parts[2].exercises + parts[3].exercises} />
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
